Create table Buildings (
Bld_Code varchar(10) Primary key,
Bld_Name varchar(80),
Bld_Desc varchar(500)
)

SELECT AddGeometryColumn
('public','buildings','geom',4326,'POLYGON',2);

SELECT AddGeometryColumn
('public','buildings','geom_center',4326,'POINT',2);


